import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs/Observable'
import 'rxjs/add/observable/timer'
import 'rxjs/add/operator/map'
import { Pipe, PipeTransform } from '@angular/core';

@Component({
  selector: "app-pomodoro",
  templateUrl: "./pomodoro.component.html",
  styleUrls: ["./pomodoro.component.css"]
})
export class PomodoroComponent implements OnInit{
  counter = 25;
  pomodoro = 25;
  shortBreak = 5;
  longBreak = 15;
  numberOfLoops = 2;
  loop = 0;
  barMaxWidth: number;
  tick = 1000;
  paused = false;
  state = 'pomodoro';
  shortPauseUsed = false;
  countDown: any;
  snd = new Audio("data:audio/wav;base64,");

  ngOnInit() {
    this.paused = true;
    this.setCounter(this.pomodoro);
    this.countDown = Observable.timer(0, this.tick)
    .map(() => !this.paused && this.counter > 0  ? this.barMove() : this.nextLoop());
  }

  public nextStep() {
    this.paused = false;
    this.nextLoop();
    this.paused = true;
  }

  public pauseCounter() {
    this.paused = true;
  }

  public resumeCounter() {
    this.paused = false;
  }

  public setPomodoro() {
    this.paused = true;
    document.getElementById("myBar").style.width = '0%';
    this.setCounter(this.pomodoro);
    this.state = 'pomodoro';
  }

  public setShortBreak() {
    this.paused = true;
    document.getElementById("myBar").style.width = '0%';
    this.setCounter(this.shortBreak);
    this.state = 'shortBreak';
  }

  public setLongBreak() {
    this.paused = true;
    document.getElementById("myBar").style.width = '0%';
    this.setCounter(this.longBreak);
    this.state = 'longBreak';
  }

  private nextLoop(): number {
    if(!this.paused) {
      this.snd.play();
      document.getElementById("myBar").style.width = '0%';
      if ((this.loop + 1) === (this.numberOfLoops * 4)) {
        this.paused = true;
        this.loop = 0;
      } else {
        this.loop++;
      }
        switch (this.state) {
          case "pomodoro":
            if(this.shortPauseUsed) {
              this.setCounter(this.longBreak);
              this.state = 'longBreak';
            } else {
              this.setCounter(this.shortBreak);
              this.state = 'shortBreak';
            }
              this.shortPauseUsed = false;
              break;
          case "shortBreak":
            this.setCounter(this.pomodoro);
            this.shortPauseUsed = true;
            this.state = 'pomodoro';
              break;
          case "longBreak":          
            this.setCounter(this.pomodoro);
            this.state = 'pomodoro';
              break;
          default:
            this.setCounter(0);
            this.state = 'pomodoro';
      }
    }
    return this.counter;
  }

private setCounter(counter: number) {
  this.counter = counter * 60;
  this.barMaxWidth = this.counter;
}

  public openForm() {
    document.getElementById("myForm").style.display = "block";
  }

  public closeForm() {
    document.getElementById("myForm").style.display = "none";
  }

  public setVariables() {
    this.closeForm();
    this.ngOnInit();
  }

  public barMove(): number {
      let barElem = document.getElementById("myBar");
      let step = 1 - (--this.counter / this.barMaxWidth);
      barElem.style.width = (step * 100) + "%";
      return this.counter;
  }
}

@Pipe({
  name: 'formatTime'
})

export class FormatTimePipe implements PipeTransform {

  transform(value: number): string {
    const minutes: number = Math.floor(value / 60);
    return ('00' + minutes).slice(-2) + ':' + ('00' + Math.floor(value - minutes * 60)).slice(-2);
  }

}